Here is an example that demonstrates how to add a text in English,
Russian and Chinese languages onto a PDF page:

''' <summary>
''' Creates page of PDF document with English, Russian and Chinese text.
''' </summary>
''' <param name="pdfFilename">The filename of PDF document.</param>
Public Shared Sub CreatePdfPageWithEnRuCh(pdfFilename As String)
    ' open PDF document
    Using document As New PdfDocument(pdfFilename)
        ' create empty PDF page
        Dim page As New PdfPage(document, PaperSizeKind.A4)
        ' add page to PDF document
        document.Pages.Add(page)

        ' text size
        Dim fontSize As Single = 24F
        ' text brush
        Dim brush As New PdfBrush(Color.Black)

        ' get names of font
        Dim fontNames As String() = PdfFontManager.GetSystemTrueTypeFontNames()

        ' create fornt for English text
        Dim englishFont As PdfFont = Nothing
        ' if "Microsoft Himalaya" font exists
        If Array.IndexOf(fontNames, "Microsoft Himalaya") >= 0 Then
            ' create PDF font based on "Microsoft Himalaya" font
            englishFont = document.FontManager.CreateSimpleFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("Microsoft Himalaya"))
        Else
            ' if "Microsoft Himalaya" font does NOT exist
            Throw New Exception("'Microsoft Himalaya' font is not found in the system.")
        End If

        ' create font for Russian text
        Dim russianFont As PdfFont = Nothing
        ' if "Times New Roman" font exists
        If Array.IndexOf(fontNames, "Times New Roman") >= 0 Then
            ' create PDF font based on "Times New Roman" font
            russianFont = document.FontManager.CreateCIDFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("Times New Roman"))
        Else
            ' if "Times New Roman" font does NOT exist
            Throw New Exception("'Times New Roman' font is not found in the system.")
        End If

        ' create font for Chinese text
        Dim chineseFont As PdfFont = Nothing
        ' if "SimSun-ExtB" font exists
        If Array.IndexOf(fontNames, "SimSun-ExtB") >= 0 Then
            ' create PDF font based on "SimSun-ExtB" font
            chineseFont = document.FontManager.CreateCIDFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("SimSun-ExtB"))
        Else
            ' if "SimSun-ExtB" font does NOT exist
            Throw New Exception("'SimSun-ExtB' font is not found in the system.")
        End If

        ' get PDF graphics of page
        Using graphics As PdfGraphics = page.GetGraphics()
            ' location of English text
            Dim location As New PointF(50, page.Size.Height - 50)
            ' draw English text
            graphics.DrawString("Hello", englishFont, fontSize, brush, location)

            ' location of Russian text
            location.Y -= 50
            ' draw Russian text
            graphics.DrawString("Привет", russianFont, fontSize, brush, location)

            ' location of Chinese text
            location.Y -= 50
            ' draw Chinese text
            graphics.DrawString("您好", chineseFont, fontSize, brush, location)
        End Using

        ' save changes to a file
        document.SaveChanges()
    End Using
End Sub


/// <summary>
/// Creates page of PDF document with English, Russian and Chinese text.
/// </summary>
/// <param name="pdfFilename">The filename of PDF document.</param>
public static void CreatePdfPageWithEnRuCh(string pdfFilename)
{
    // open PDF document
    using (PdfDocument document = new PdfDocument(pdfFilename))
    {
        // create empty PDF page
        PdfPage page = new PdfPage(document, PaperSizeKind.A4);
        // add page to PDF document
        document.Pages.Add(page);

        // text size
        float fontSize = 24f;
        // text brush
        PdfBrush brush = new PdfBrush(Color.Black);

        // get names of font
        string[] fontNames = PdfFontManager.GetSystemTrueTypeFontNames();

        // create fornt for English text
        PdfFont englishFont = null;
        // if "Microsoft Himalaya" font exists
        if (Array.IndexOf(fontNames, "Microsoft Himalaya") >= 0)
            // create PDF font based on "Microsoft Himalaya" font
            englishFont = document.FontManager.CreateSimpleFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("Microsoft Himalaya"));
        // if "Microsoft Himalaya" font does NOT exist
        else
            throw new Exception("'Microsoft Himalaya' font is not found in the system.");

        // create font for Russian text
        PdfFont russianFont = null;
        // if "Times New Roman" font exists
        if (Array.IndexOf(fontNames, "Times New Roman") >= 0)
            // create PDF font based on "Times New Roman" font
            russianFont = document.FontManager.CreateCIDFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("Times New Roman"));
        // if "Times New Roman" font does NOT exist
        else
            throw new Exception("'Times New Roman' font is not found in the system.");

        // create font for Chinese text
        PdfFont chineseFont = null;
        // if "SimSun-ExtB" font exists
        if (Array.IndexOf(fontNames, "SimSun-ExtB") >= 0)
            // create PDF font based on "SimSun-ExtB" font
            chineseFont = document.FontManager.CreateCIDFontFromTrueTypeFont(PdfFontManager.GetSystemTrueTypeFontFileName("SimSun-ExtB"));
        // if "SimSun-ExtB" font does NOT exist
        else
            throw new Exception("'SimSun-ExtB' font is not found in the system.");

        // get PDF graphics of page
        using (PdfGraphics graphics = page.GetGraphics())
        {
            // location of English text
            PointF location = new PointF(50, page.Size.Height - 50);
            // draw English text
            graphics.DrawString("Hello", englishFont, fontSize, brush, location);

            // location of Russian text
            location.Y -= 50;
            // draw Russian text
            graphics.DrawString("Привет", russianFont, fontSize, brush, location);

            // location of Chinese text
            location.Y -= 50;
            // draw Chinese text
            graphics.DrawString("您好", chineseFont, fontSize, brush, location);
        }

        // save changes to a file
        document.SaveChanges();
    }
}

Here is an example that shows how to pack all fonts of PDF document:

''' <summary>
''' Removes all unused characters from fonts of PDF document.
''' </summary>
''' <param name="pdfFilename">The filename of PDF document.</param>
''' <param name="resultFilename">The filename of resulting PDF document.</param>
Public Shared Sub PackAllFontsOfDocument(pdfFilename As String, resultFilename As String)
    Using document As New PdfDocument(pdfFilename)
        ' pack all fonts
        document.FontManager.PackAllFonts()
        ' pack and save document to new location
        document.Pack(resultFilename)
    End Using
End Sub


/// <summary>
/// Removes all unused characters from fonts of PDF document.
/// </summary>
/// <param name="pdfFilename">The filename of PDF document.</param>
/// <param name="resultFilename">The filename of resulting PDF document.</param>
public static void PackAllFontsOfDocument(string pdfFilename, string resultFilename)
{
    using (PdfDocument document = new PdfDocument(pdfFilename))
    {
        // pack all fonts
        document.FontManager.PackAllFonts();
        // pack and save document to new location
        document.Pack(resultFilename);
    }
}

Here is an example that shows how to embed all external and standard fonts into PDF document:

''' <summary>
''' Embeds all fonts into PDF document.
''' </summary>
''' <param name="pdfFilename">The filename of PDF document.</param>
''' <param name="pathToFonts">The path to available fonts.</param>
''' <param name="resultFilename">The filename of resulting PDF document.</param>
Public Shared Sub EmbedAllFontsIntoDocument(pdfFilename As String, pathToFonts As String, resultFilename As String)
    Using document As New PdfDocument(pdfFilename)
        ' set a font programs controller
        document.FontProgramsController = New UserAndSystemFontProgramsController(pathToFonts)
        ' embed all fonts
        document.FontManager.EmbedAllFonts()
        ' pack and save document to new location
        document.Pack(resultFilename)
    End Using
End Sub


/// <summary>
/// Embeds all fonts into PDF document.
/// </summary>
/// <param name="pdfFilename">The filename of PDF document.</param>
/// <param name="pathToFonts">The path to available fonts.</param>
/// <param name="resultFilename">The filename of resulting PDF document.</param>
public static void EmbedAllFontsIntoDocument(string pdfFilename, string pathToFonts, string resultFilename)
{
    using (PdfDocument document = new PdfDocument(pdfFilename))
    {
        // set a font programs controller
        document.FontProgramsController = new UserAndSystemFontProgramsController(pathToFonts);
        // embed all fonts
        document.FontManager.EmbedAllFonts();
        // pack and save document to new location
        document.Pack(resultFilename);
    }
}

Here is an example that shows how to obfuscate encoding of embedded fonts in PDF document:

''' <summary>
''' Obfuscates encoding of all fonts of PDF document.
''' </summary>
''' <param name="pdfFilename">The filename of PDF document.</param>
''' <param name="resultFilename">The filename of resulting PDF document.</param>
Public Shared Sub ObfuscateEncodingOfAllFonts(pdfFilename As String, resultFilename As String)
    Using document As New PdfDocument(pdfFilename)
        ' create obfuscator
        Dim textEncodingObfuscator As New PdfTextEncodingObfuscator()
        Try
            ' obfuscate all fonts
            textEncodingObfuscator.Obfuscate(document)
            ' pack and save document to new location
            document.Pack(resultFilename)
        Catch ex As Exception
            Console.WriteLine(ex.Message)
        End Try
    End Using
End Sub


/// <summary>
/// Obfuscates encoding of all fonts of PDF document.
/// </summary>
/// <param name="pdfFilename">The filename of PDF document.</param>
/// <param name="resultFilename">The filename of resulting PDF document.</param>
public static void ObfuscateEncodingOfAllFonts(string pdfFilename, string resultFilename)
{
    using (PdfDocument document = new PdfDocument(pdfFilename))
    {
        // create obfuscator
        PdfTextEncodingObfuscator textEncodingObfuscator = new PdfTextEncodingObfuscator();
        try
        {
            // obfuscate all fonts
            textEncodingObfuscator.Obfuscate(document);
            // pack and save document to new location
            document.Pack(resultFilename);
        }
        catch (Exception ex)
        {
            Console.WriteLine(ex.Message);
        }
    }
}

Here is an example that shows how to draw image figure on PDF page:

Public Shared Sub CreatePdfDocument(pdfFilename As String, imageFilename As String)
    ' create PDF document
    Using document As New PdfDocument(pdfFilename, PdfFormat.Pdf_14)
        ' open image
        Using image As New VintasoftImage(imageFilename)
            ' add new page to PDF document
            Dim page As PdfPage = document.Pages.Add(PaperSizeKind.A4)
            ' add image resource to PDF document
            Dim imageResource As New PdfImageResource(document, image, PdfCompression.Auto)

            ' create layout panel
            Dim rootLayout As New AlignmentPanel()
            rootLayout.Location = page.MediaBox.Location
            rootLayout.Size = page.MediaBox.Size
            ' set layout panel margin (5 mm)
            Dim margin As Double = UnitOfMeasureConverter.ConvertToPdfUserUnits(5, UnitOfMeasure.Millimeters)
            rootLayout.Margin = New PdfContentPadding(margin)

            ' create image figure
            Dim imageFigure As New ImageFigure(imageResource)
            ' maintain aspect ratio of image
            imageFigure.MaintainAspectRatio = True
            ' add image figure to layout panel
            rootLayout.Add(imageFigure)

            ' draw figures on graphics of PDF page
            Using g As PdfGraphics = page.GetGraphics()
                rootLayout.Draw(g)
            End Using

            ' save document changes
            document.SaveChanges()
        End Using
    End Using
End Sub


public static void CreatePdfDocument(string pdfFilename, string imageFilename)
{
    // create PDF document
    using (PdfDocument document = new PdfDocument(pdfFilename, PdfFormat.Pdf_14))
    {// open image
        using (VintasoftImage image = new VintasoftImage(imageFilename))
        {
            // add new page to PDF document
            PdfPage page = document.Pages.Add(PaperSizeKind.A4);
            // add image resource to PDF document
            PdfImageResource imageResource = new PdfImageResource(document, image, PdfCompression.Auto);

            // create layout panel
            AlignmentPanel rootLayout = new AlignmentPanel();
            rootLayout.Location = page.MediaBox.Location;
            rootLayout.Size = page.MediaBox.Size;
            // set layout panel margin (5 mm)
            double margin = UnitOfMeasureConverter.ConvertToPdfUserUnits(5, UnitOfMeasure.Millimeters);
            rootLayout.Margin = new PdfContentPadding(margin);

            // create image figure
            ImageFigure imageFigure = new ImageFigure(imageResource);
            // maintain aspect ratio of image
            imageFigure.MaintainAspectRatio = true;
            // add image figure to layout panel
            rootLayout.Add(imageFigure);

            // draw figures on graphics of PDF page
            using (PdfGraphics g = page.GetGraphics())
                rootLayout.Draw(g);

            // save document changes
            document.SaveChanges();
        }
    }
}

Here is an example that shows how to override rendering algorithm and render PDF with different setting:
Imports System.Drawing Imports System.Drawing.Drawing2D Imports System.IO
Imports Vintasoft.Imaging Imports Vintasoft.Imaging.Codecs.Encoders Imports Vintasoft.Imaging.ImageProcessing Imports Vintasoft.Imaging.ImageProcessing.Color Imports Vintasoft.Imaging.Pdf Imports Vintasoft.Imaging.Pdf.Content Imports Vintasoft.Imaging.Pdf.Tree Imports Vintasoft.Imaging.Pdf.Tree.Patterns Imports Vintasoft.Imaging.Pdf.Tree.ShadingPatterns
Class PdfContentRendererExample ''' <summary> ''' Shows how manage rendering of PDF page, i.e. render PDF page without images, ''' render PDF page without text, etc ''' </summary> ''' <param name="pdfFilename">The filename of PDF document.</param> Public Shared Sub TestCustomContentRenderer(pdfFilename As String) ' output TIFF file name format string Dim outputFileName As String = Path.Combine(Path.GetDirectoryName(pdfFilename), Path.GetFileNameWithoutExtension(pdfFilename)) outputFileName = outputFileName & "_{0}.tif"
' custom renderer Dim renderer As CustomContentRenderer
' create image collection Using images As New ImageCollection() ' open source PDF file images.Add(pdfFilename)
' render PDF pages without images renderer = New CustomContentRenderer() renderer.DrawImages = False RenderPdfUseContentRenderer(images, String.Format(outputFileName, "withoutImages"), renderer)
' render PDF pages and invert rendered images renderer = New CustomContentRenderer() renderer.ImageProcessing = New InvertCommand() RenderPdfUseContentRenderer(images, String.Format(outputFileName, "invertImages"), renderer)
' render PDF pages without path filling renderer = New CustomContentRenderer() renderer.FillAreaUseShadingPatterns = False renderer.FillPaths = False renderer.FillPathsUseShadingPatterns = False renderer.FillPathsUseTilingPatterns = False RenderPdfUseContentRenderer(images, String.Format(outputFileName, "withoutFillPath"), renderer)
' render PDF pages without path drawing renderer = New CustomContentRenderer() renderer.DrawPaths = False RenderPdfUseContentRenderer(images, String.Format(outputFileName, "withoutDrawPath"), renderer)
' render PDF pages without text renderer = New CustomContentRenderer() renderer.DrawText = False RenderPdfUseContentRenderer(images, String.Format(outputFileName, "withoutText"), renderer)
' clear image collection and dispose images images.ClearAndDisposeItems() End Using End Sub
''' <summary> ''' Renders the PDF file using specified content renderer. ''' </summary> ''' <param name="images">The images to render.</param> ''' <param name="outputTiffFileName">Name of the output tiff file.</param> ''' <param name="renderer">The renderer.</param> Public Shared Sub RenderPdfUseContentRenderer(images As ImageCollection, outputTiffFileName As String, renderer As PdfContentRenderer) ' create PDF rendering settings Dim settings As New PdfRenderingSettings() ' specify the custom PDF content renderer settings.ContentRenderer = renderer ' specify PDF rendering settings for images (PDF pages) images.SetRenderingSettings(settings)
' create TIFF encoder Using encoder As New TiffEncoder() ' save PDF pages to a TIFF file images.SaveSync(outputTiffFileName, encoder) End Using End Sub
''' <summary> ''' Provides functionality for overriding algorithms of PDF content rendering. ''' </summary> Public Class CustomContentRenderer Inherits PdfContentRenderer
#Region "Fields"
''' <summary> ''' Determines that string is drawing. ''' </summary> Private _stringDrawing As Boolean = False
#End Region
#Region "Properties"
Private _drawForms As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw form XObjects on PDF page. ''' </summary> Public Property DrawForms() As Boolean Get Return _drawForms End Get Set _drawForms = value End Set End Property
Private _drawImages As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw images on PDF page. ''' </summary> Public Property DrawImages() As Boolean Get Return _drawImages End Get Set _drawImages = value End Set End Property
Private _drawInlineImages As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw inline images on PDF page. ''' </summary> Public Property DrawInlineImages() As Boolean Get Return _drawInlineImages End Get Set _drawInlineImages = value End Set End Property
Private _drawPaths As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw graphics paths on PDF page. ''' </summary> ''' </value> Public Property DrawPaths() As Boolean Get Return _drawPaths End Get Set _drawPaths = value End Set End Property
Private _fillPaths As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must fill graphics paths on PDF page. ''' </summary> Public Property FillPaths() As Boolean Get Return _fillPaths End Get Set _fillPaths = value End Set End Property
Private _fillPathsUseTilingPatterns As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must fill ''' graphics paths using tiling patterns on PDF page. ''' </summary> Public Property FillPathsUseTilingPatterns() As Boolean Get Return _fillPathsUseTilingPatterns End Get Set _fillPathsUseTilingPatterns = value End Set End Property
Private _fillPathsUseShadingPatterns As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must fill ''' graphics paths using shading patterns on PDF page. ''' </summary> Public Property FillPathsUseShadingPatterns() As Boolean Get Return _fillPathsUseShadingPatterns End Get Set _fillPathsUseShadingPatterns = value End Set End Property
Private _fillAreaUseShadingPatterns As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must fill ''' all visible areas using tiling patterns on PDF page. ''' </summary> Public Property FillAreaUseShadingPatterns() As Boolean Get Return _fillAreaUseShadingPatterns End Get Set _fillAreaUseShadingPatterns = value End Set End Property
Private _drawText As Boolean = True ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw ''' text on PDF page. ''' </summary> Public Property DrawText() As Boolean Get Return _drawText End Get Set _drawText = value End Set End Property
Private _drawInvisibleText As Boolean = False ''' <summary> ''' Gets or sets a value indicating whether the renderer must draw ''' invisible text on PDF page. ''' </summary> Public Property DrawInvisibleText() As Boolean Get Return _drawInvisibleText End Get Set _drawInvisibleText = value End Set End Property
Private _linesWeigth As Single = 1 ''' <summary> ''' Gets or sets the lines weigth, in percents. ''' </summary> Public Property LinesWeigth() As Single Get Return _linesWeigth End Get Set _linesWeigth = value End Set End Property
Private _imageProcessing As ProcessingCommandBase = Nothing ''' <summary> ''' Gets or sets the image processing command that should be applied to an image ''' before image will be drawn on PDF page. ''' </summary> Public Property ImageProcessing() As ProcessingCommandBase Get Return _imageProcessing End Get Set _imageProcessing = value End Set End Property
#End Region
#Region "Methods"
''' <summary> ''' Draws a text string. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="charCodes">The codes, in font encoding, of text characters.</param> Public Overrides Sub DrawString(context As PdfContentRenderingContext, charCodes As ULong()) ' if text must be drawn AND text is visible If _drawText AndAlso context.GraphicsState.TextRenderingMode <> TextRenderingMode.Invisible Then _stringDrawing = True MyBase.DrawString(context, charCodes) _stringDrawing = False ' if invisible text must be drawn AND text is invisible ElseIf _drawInvisibleText AndAlso context.GraphicsState.TextRenderingMode = TextRenderingMode.Invisible Then context.GraphicsState.TextRenderingMode = TextRenderingMode.Fill MyBase.DrawString(context, charCodes) context.GraphicsState.TextRenderingMode = TextRenderingMode.Invisible End If End Sub
''' <summary> ''' Draws path using specified pen. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="path">The path to draw.</param> ''' <param name="pen">The pen that should be used for drawing a path.</param> Public Overrides Sub DrawPath(context As PdfContentRenderingContext, path As GraphicsPath, pen As Pen) ' if path must be drawn If (_drawPaths AndAlso Not _stringDrawing) OrElse (_drawText AndAlso _stringDrawing) Then ' if drawing path is not a drawing path of text symbol If _linesWeigth <> 1 AndAlso Not _stringDrawing Then Dim oldWidth As Single = pen.Width pen.Width *= _linesWeigth MyBase.DrawPath(context, path, pen) pen.Width = oldWidth Else MyBase.DrawPath(context, path, pen) End If End If End Sub
''' <summary> ''' Fills path using specified brush. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="path">The path to fill.</param> ''' <param name="brush">The brush that should be used for filling a path.</param> Public Overrides Sub FillPath(context As PdfContentRenderingContext, path As GraphicsPath, brush As Brush) ' if path must be filled If (_fillPaths AndAlso Not _stringDrawing) OrElse (_drawText AndAlso _stringDrawing) Then MyBase.FillPath(context, path, brush) End If End Sub
''' <summary> ''' Fills path using specified pattern. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="path">The path to fill.</param> ''' <param name="pattern">The pattern that should be used for filling a path.</param> Public Overrides Sub FillPath(context As PdfContentRenderingContext, path As GraphicsPath, pattern As PatternBase) If Not _fillPathsUseShadingPatterns AndAlso TypeOf pattern Is ShadingPattern Then Return End If If Not _fillPathsUseTilingPatterns AndAlso TypeOf pattern Is TilingPattern Then Return End If MyBase.FillPath(context, path, pattern) End Sub
''' <summary> ''' Fills all visible areas using specified shading pattern. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="shadingPattern">The shading pattern.</param> Public Overrides Sub FillArea(context As PdfContentRenderingContext, shadingPattern As ShadingPatternBase) If _fillAreaUseShadingPatterns Then MyBase.FillArea(context, shadingPattern) End If End Sub
''' <summary> ''' Draws a form XObject. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="formResource">The form XObject to draw.</param> Public Overrides Sub DrawFormXObject(context As PdfContentRenderingContext, formResource As PdfFormXObjectResource) If _drawForms Then MyBase.DrawFormXObject(context, formResource) End If End Sub
''' <summary> ''' Draws an image resource. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="imageResource">The image XObject resource to draw.</param> Public Overrides Sub DrawImageResource(context As PdfContentRenderingContext, imageResource As PdfImageResource) If Not _drawImages AndAlso Not imageResource.IsInline Then Return End If If Not _drawInlineImages AndAlso imageResource.IsInline Then Return End If MyBase.DrawImageResource(context, imageResource) End Sub
''' <summary> ''' Draws an image. ''' </summary> ''' <param name="context">The rendering context.</param> ''' <param name="image">The image to draw.</param> ''' <param name="points">Array of three System.Drawing.PointF structures that ''' define a parallelogram on rendering content where image must be drawn.</param> Public Overrides Sub DrawImage(context As PdfContentRenderingContext, image As VintasoftImage, points As PointF()) ' if image processing muse be applied to an image If ImageProcessing IsNot Nothing AndAlso ImageProcessing.IsPixelFormatSupported(image.PixelFormat) Then SyncLock image Using tempImage As VintasoftImage = ImageProcessing.Execute(image) MyBase.DrawImage(context, tempImage, points) End Using End SyncLock Else MyBase.DrawImage(context, image, points) End If End Sub
#End Region
End Class End Class
using System.Drawing; using System.Drawing.Drawing2D; using System.IO;
using Vintasoft.Imaging; using Vintasoft.Imaging.Codecs.Encoders; using Vintasoft.Imaging.ImageProcessing; using Vintasoft.Imaging.ImageProcessing.Color; using Vintasoft.Imaging.Pdf; using Vintasoft.Imaging.Pdf.Content; using Vintasoft.Imaging.Pdf.Tree; using Vintasoft.Imaging.Pdf.Tree.Patterns; using Vintasoft.Imaging.Pdf.Tree.ShadingPatterns;
class PdfContentRendererExample { /// <summary> /// Shows how manage rendering of PDF page, i.e. render PDF page without images, /// render PDF page without text, etc /// </summary> /// <param name="pdfFilename">The filename of PDF document.</param> public static void TestCustomContentRenderer(string pdfFilename) { // output TIFF file name format string string outputFileName = Path.Combine(Path.GetDirectoryName(pdfFilename), Path.GetFileNameWithoutExtension(pdfFilename)); outputFileName = outputFileName + "_{0}.tif";
// custom renderer CustomContentRenderer renderer;
// create image collection using (ImageCollection images = new ImageCollection()) { // open source PDF file images.Add(pdfFilename);
// render PDF pages without images renderer = new CustomContentRenderer(); renderer.DrawImages = false; RenderPdfUseContentRenderer(images, string.Format(outputFileName, "withoutImages"), renderer);
// render PDF pages and invert rendered images renderer = new CustomContentRenderer(); renderer.ImageProcessing = new InvertCommand(); RenderPdfUseContentRenderer(images, string.Format(outputFileName, "invertImages"), renderer);
// render PDF pages without path filling renderer = new CustomContentRenderer(); renderer.FillAreaUseShadingPatterns = false; renderer.FillPaths = false; renderer.FillPathsUseShadingPatterns = false; renderer.FillPathsUseTilingPatterns = false; RenderPdfUseContentRenderer(images, string.Format(outputFileName, "withoutFillPath"), renderer);
// render PDF pages without path drawing renderer = new CustomContentRenderer(); renderer.DrawPaths = false; RenderPdfUseContentRenderer(images, string.Format(outputFileName, "withoutDrawPath"), renderer);
// render PDF pages without text renderer = new CustomContentRenderer(); renderer.DrawText = false; RenderPdfUseContentRenderer(images, string.Format(outputFileName, "withoutText"), renderer);
// clear image collection and dispose images images.ClearAndDisposeItems(); } }
/// <summary> /// Renders the PDF file using specified content renderer. /// </summary> /// <param name="images">The images to render.</param> /// <param name="outputTiffFileName">Name of the output tiff file.</param> /// <param name="renderer">The renderer.</param> public static void RenderPdfUseContentRenderer( ImageCollection images, string outputTiffFileName, PdfContentRenderer renderer) { // create PDF rendering settings PdfRenderingSettings settings = new PdfRenderingSettings(); // specify the custom PDF content renderer settings.ContentRenderer = renderer; // specify PDF rendering settings for images (PDF pages) images.SetRenderingSettings(settings);
// create TIFF encoder using (TiffEncoder encoder = new TiffEncoder()) // save PDF pages to a TIFF file images.SaveSync(outputTiffFileName, encoder); }
/// <summary> /// Provides functionality for overriding algorithms of PDF content rendering. /// </summary> public class CustomContentRenderer : PdfContentRenderer {
#region Fields
/// <summary> /// Determines that string is drawing. /// </summary> bool _stringDrawing = false;
#endregion
#region Properties
bool _drawForms = true; /// <summary> /// Gets or sets a value indicating whether the renderer must draw form XObjects on PDF page. /// </summary> public bool DrawForms { get { return _drawForms; } set { _drawForms = value; } }
bool _drawImages = true; /// <summary> /// Gets or sets a value indicating whether the renderer must draw images on PDF page. /// </summary> public bool DrawImages { get { return _drawImages; } set { _drawImages = value; } }
bool _drawInlineImages = true; /// <summary> /// Gets or sets a value indicating whether the renderer must draw inline images on PDF page. /// </summary> public bool DrawInlineImages { get { return _drawInlineImages; } set { _drawInlineImages = value; } }
bool _drawPaths = true; /// <summary> /// Gets or sets a value indicating whether the renderer must draw graphics paths on PDF page. /// </summary> /// </value> public bool DrawPaths { get { return _drawPaths; } set { _drawPaths = value; } }
bool _fillPaths = true; /// <summary> /// Gets or sets a value indicating whether the renderer must fill graphics paths on PDF page. /// </summary> public bool FillPaths { get { return _fillPaths; } set { _fillPaths = value; } }
bool _fillPathsUseTilingPatterns = true; /// <summary> /// Gets or sets a value indicating whether the renderer must fill /// graphics paths using tiling patterns on PDF page. /// </summary> public bool FillPathsUseTilingPatterns { get { return _fillPathsUseTilingPatterns; } set { _fillPathsUseTilingPatterns = value; } }
bool _fillPathsUseShadingPatterns = true; /// <summary> /// Gets or sets a value indicating whether the renderer must fill /// graphics paths using shading patterns on PDF page. /// </summary> public bool FillPathsUseShadingPatterns { get { return _fillPathsUseShadingPatterns; } set { _fillPathsUseShadingPatterns = value; } }
bool _fillAreaUseShadingPatterns = true; /// <summary> /// Gets or sets a value indicating whether the renderer must fill /// all visible areas using tiling patterns on PDF page. /// </summary> public bool FillAreaUseShadingPatterns { get { return _fillAreaUseShadingPatterns; } set { _fillAreaUseShadingPatterns = value; } }
bool _drawText = true; /// <summary> /// Gets or sets a value indicating whether the renderer must draw /// text on PDF page. /// </summary> public bool DrawText { get { return _drawText; } set { _drawText = value; } }
bool _drawInvisibleText = false; /// <summary> /// Gets or sets a value indicating whether the renderer must draw /// invisible text on PDF page. /// </summary> public bool DrawInvisibleText { get { return _drawInvisibleText; } set { _drawInvisibleText = value; } }
float _linesWeigth = 1; /// <summary> /// Gets or sets the lines weigth, in percents. /// </summary> public float LinesWeigth { get { return _linesWeigth; } set { _linesWeigth = value; } }
ProcessingCommandBase _imageProcessing = null; /// <summary> /// Gets or sets the image processing command that should be applied to an image /// before image will be drawn on PDF page. /// </summary> public ProcessingCommandBase ImageProcessing { get { return _imageProcessing; } set { _imageProcessing = value; } }
#endregion
#region Methods
/// <summary> /// Draws a text string. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="charCodes">The codes, in font encoding, of text characters.</param> public override void DrawString(PdfContentRenderingContext context, ulong[] charCodes) { // if text must be drawn AND text is visible if (_drawText && context.GraphicsState.TextRenderingMode != TextRenderingMode.Invisible) { _stringDrawing = true; base.DrawString(context, charCodes); _stringDrawing = false; } // if invisible text must be drawn AND text is invisible else if (_drawInvisibleText && context.GraphicsState.TextRenderingMode == TextRenderingMode.Invisible) { context.GraphicsState.TextRenderingMode = TextRenderingMode.Fill; base.DrawString(context, charCodes); context.GraphicsState.TextRenderingMode = TextRenderingMode.Invisible; } }
/// <summary> /// Draws path using specified pen. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="path">The path to draw.</param> /// <param name="pen">The pen that should be used for drawing a path.</param> public override void DrawPath(PdfContentRenderingContext context, GraphicsPath path, Pen pen) { // if path must be drawn if ((_drawPaths && !_stringDrawing) || (_drawText && _stringDrawing)) { // if drawing path is not a drawing path of text symbol if (_linesWeigth != 1 && !_stringDrawing) { float oldWidth = pen.Width; pen.Width *= _linesWeigth; base.DrawPath(context, path, pen); pen.Width = oldWidth; } else { base.DrawPath(context, path, pen); } } }
/// <summary> /// Fills path using specified brush. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="path">The path to fill.</param> /// <param name="brush">The brush that should be used for filling a path.</param> public override void FillPath(PdfContentRenderingContext context, GraphicsPath path, Brush brush) { // if path must be filled if ((_fillPaths && !_stringDrawing) || (_drawText && _stringDrawing)) base.FillPath(context, path, brush); }
/// <summary> /// Fills path using specified pattern. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="path">The path to fill.</param> /// <param name="pattern">The pattern that should be used for filling a path.</param> public override void FillPath( PdfContentRenderingContext context, GraphicsPath path, PatternBase pattern) { if (!_fillPathsUseShadingPatterns && pattern is ShadingPattern) return; if (!_fillPathsUseTilingPatterns && pattern is TilingPattern) return; base.FillPath(context, path, pattern); }
/// <summary> /// Fills all visible areas using specified shading pattern. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="shadingPattern">The shading pattern.</param> public override void FillArea( PdfContentRenderingContext context, ShadingPatternBase shadingPattern) { if (_fillAreaUseShadingPatterns) base.FillArea(context, shadingPattern); }
/// <summary> /// Draws a form XObject. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="formResource">The form XObject to draw.</param> public override void DrawFormXObject( PdfContentRenderingContext context, PdfFormXObjectResource formResource) { if (_drawForms) base.DrawFormXObject(context, formResource); }
/// <summary> /// Draws an image resource. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="imageResource">The image XObject resource to draw.</param> public override void DrawImageResource( PdfContentRenderingContext context, PdfImageResource imageResource) { if (!_drawImages && !imageResource.IsInline) return; if (!_drawInlineImages && imageResource.IsInline) return; base.DrawImageResource(context, imageResource); }
/// <summary> /// Draws an image. /// </summary> /// <param name="context">The rendering context.</param> /// <param name="image">The image to draw.</param> /// <param name="points">Array of three System.Drawing.PointF structures that /// define a parallelogram on rendering content where image must be drawn.</param> public override void DrawImage( PdfContentRenderingContext context, VintasoftImage image, PointF[] points) { // if image processing muse be applied to an image if (ImageProcessing != null && ImageProcessing.IsPixelFormatSupported(image.PixelFormat)) { lock (image) { using (VintasoftImage tempImage = ImageProcessing.Execute(image)) base.DrawImage(context, tempImage, points); } } else { base.DrawImage(context, image, points); } }
#endregion
} }

Here is an example that shows how to draw image figure on PDF page:

Public Shared Sub CreatePdfDocument(pdfFilename As String, imageFilename As String)
    ' create PDF document
    Using document As New PdfDocument(pdfFilename, PdfFormat.Pdf_14)
        ' open image
        Using image As New VintasoftImage(imageFilename)
            ' add new page to PDF document
            Dim page As PdfPage = document.Pages.Add(PaperSizeKind.A4)
            ' add image resource to PDF document
            Dim imageResource As New PdfImageResource(document, image, PdfCompression.Auto)

            ' create layout panel
            Dim rootLayout As New AlignmentPanel()
            rootLayout.Location = page.MediaBox.Location
            rootLayout.Size = page.MediaBox.Size
            ' set layout panel margin (5 mm)
            Dim margin As Double = UnitOfMeasureConverter.ConvertToPdfUserUnits(5, UnitOfMeasure.Millimeters)
            rootLayout.Margin = New PdfContentPadding(margin)

            ' create image figure
            Dim imageFigure As New ImageFigure(imageResource)
            ' maintain aspect ratio of image
            imageFigure.MaintainAspectRatio = True
            ' add image figure to layout panel
            rootLayout.Add(imageFigure)

            ' draw figures on graphics of PDF page
            Using g As PdfGraphics = page.GetGraphics()
                rootLayout.Draw(g)
            End Using

            ' save document changes
            document.SaveChanges()
        End Using
    End Using
End Sub


public static void CreatePdfDocument(string pdfFilename, string imageFilename)
{
    // create PDF document
    using (PdfDocument document = new PdfDocument(pdfFilename, PdfFormat.Pdf_14))
    {// open image
        using (VintasoftImage image = new VintasoftImage(imageFilename))
        {
            // add new page to PDF document
            PdfPage page = document.Pages.Add(PaperSizeKind.A4);
            // add image resource to PDF document
            PdfImageResource imageResource = new PdfImageResource(document, image, PdfCompression.Auto);

            // create layout panel
            AlignmentPanel rootLayout = new AlignmentPanel();
            rootLayout.Location = page.MediaBox.Location;
            rootLayout.Size = page.MediaBox.Size;
            // set layout panel margin (5 mm)
            double margin = UnitOfMeasureConverter.ConvertToPdfUserUnits(5, UnitOfMeasure.Millimeters);
            rootLayout.Margin = new PdfContentPadding(margin);

            // create image figure
            ImageFigure imageFigure = new ImageFigure(imageResource);
            // maintain aspect ratio of image
            imageFigure.MaintainAspectRatio = true;
            // add image figure to layout panel
            rootLayout.Add(imageFigure);

            // draw figures on graphics of PDF page
            using (PdfGraphics g = page.GetGraphics())
                rootLayout.Draw(g);

            // save document changes
            document.SaveChanges();
        }
    }
}

Here is an example that demonstrates how to create an annotation, 
which consists from red rectangle:

''' <summary>
''' Creates an annotation, which consists from red rectangle.
''' </summary>
''' <param name="page">The page.</param>
''' <returns>The created annotation.</returns>
Public Shared Function CreatePdfAnnotation(page As PdfPage) As PdfAnnotation
    ' create a rectangular annotation
    Dim annotation As New PdfRectangleAnnotation(page)
    ' set interior color
    annotation.InteriorColor = Color.Red
    ' set rectangle
    annotation.Rectangle = New RectangleF(40, 40, 160, 80)
    ' create graphics for normal appearance
    Using pdfGraphics As PdfGraphics = annotation.CreateNormalAppearanceGraphics()
        ' draw an appearance of the annotation
        pdfGraphics.FillRectangle(New PdfBrush(Color.Red), 0, 0, annotation.Rectangle.Width, annotation.Rectangle.Height)
    End Using

    ' return created annotation
    Return annotation
End Function


/// <summary>
/// Creates an annotation, which consists from red rectangle.
/// </summary>
/// <param name="page">The page.</param>
/// <returns>The created annotation.</returns>
public static PdfAnnotation CreatePdfAnnotation(PdfPage page)
{
    // create a rectangular annotation
    PdfRectangleAnnotation annotation = new PdfRectangleAnnotation(page);
    // set interior color
    annotation.InteriorColor = Color.Red;
    // set rectangle
    annotation.Rectangle = new RectangleF(40, 40, 160, 80);
    // create graphics for normal appearance
    using (PdfGraphics pdfGraphics = annotation.CreateNormalAppearanceGraphics())
    {
        // draw an appearance of the annotation
        pdfGraphics.FillRectangle(
            new PdfBrush(Color.Red), 0, 0, annotation.Rectangle.Width, annotation.Rectangle.Height);
    }

    // return created annotation
    return annotation;
}

Here is an example that shows how to get information about attachments of PDF document:

''' <summary>
''' Prints the portfolio structure.
''' </summary>
''' <param name="pdfFilename">The PDF filename.</param>
Public Shared Sub PrintPortfolioStructure(pdfFilename As String)
    ' open PDF document in read-only mode
    Using document As New PdfDocument(pdfFilename, True)
        ' if PDF document does not contain portfolio
        If document.Attachments Is Nothing Then
            Console.WriteLine("Document does not have attachments (portfolio).")
            Return
        End If

        ' print inital view mode
        Console.WriteLine(String.Format("Inital View Mode = {0}", document.Attachments.View))

        ' print colors information
        If document.Attachments.Colors IsNot Nothing Then
            PrintPortfolioColors(document.Attachments.Colors)
        End If

        ' print portfolio schema
        If document.Attachments.Schema IsNot Nothing Then
            PrintPortfolioSchema(document.Attachments.Schema)
        End If

        ' print portfolio sort settings
        If document.Attachments.Sort IsNot Nothing Then
            PrintPortfolioSort(document.Attachments.Sort)
        End If

        ' print portfolio splitter bar settings
        If document.Attachments.SplitterBar IsNot Nothing Then
            PrintPortfolioSplitterBar(document.Attachments.SplitterBar)
        End If

        ' print portfolio folders and files
        If document.Attachments.RootFolder IsNot Nothing Then
            Console.WriteLine("Folder structure:")
            PrintFolderStructure(document.Attachments.RootFolder, "  ")
        Else
            Console.WriteLine("Files:")
            PrintFileInfo(document.Attachments.GetFiles(""), "  ")
        End If
    End Using
End Sub

''' <summary>
''' Prints the folder structure.
''' </summary>
''' <param name="pdfAttachmentFolder">The PDF attachment folder.</param>
Private Shared Sub PrintFolderStructure(folder As PdfAttachmentFolder, padding As String)
    Console.WriteLine(String.Format("{0}Folder: {1}", padding, folder.Name))
    padding += "  "
    PrintFileInfo(folder.Files, padding)
    Dim subFolders As PdfAttachmentFolder() = folder.Folders
    If subFolders IsNot Nothing Then
        For Each subFolder As PdfAttachmentFolder In subFolders
            PrintFolderStructure(subFolder, padding)
        Next
    End If
End Sub

''' <summary>
''' Prints the portfolio splitter bar information.
''' </summary>
''' <param name="splitterBar">The splitter bar.</param>
Private Shared Sub PrintPortfolioSplitterBar(splitterBar As PdfAttachmentCollectionSplitterBar)
    Console.WriteLine("Splitter Bar:")
    Console.WriteLine(String.Format("  Direction = {0}", splitterBar.Direction))
    Console.WriteLine(String.Format("  Position  = {0}", splitterBar.Position))
End Sub

''' <summary>
''' Prints an information for specified files.
''' </summary>
''' <param name="pdfEmbeddedFileSpecification">The PDF embedded file specifications.</param>
Private Shared Sub PrintFileInfo(fileSpecs As PdfEmbeddedFileSpecification(), padding As String)
    For Each fileSpec As PdfEmbeddedFileSpecification In fileSpecs
        Console.WriteLine(String.Format("{0}File: {1}", padding, fileSpec.Filename))
        Console.WriteLine(String.Format("{0}  CompressedSize   = {1}", padding, fileSpec.CompressedSize))
        Console.WriteLine(String.Format("{0}  UncompressedSize = {1}", padding, fileSpec.UncompressedSize))
        Console.WriteLine(String.Format("{0}  Compression      = {1}", padding, fileSpec.Compression))
        Console.WriteLine(String.Format("{0}  CreationDate     = {1}", padding, fileSpec.CreationDate))
        Console.WriteLine(String.Format("{0}  ModificationDate = {1}", padding, fileSpec.ModificationDate))
        Console.WriteLine(String.Format("{0}  Description      = {1}", padding, fileSpec.Description))
        Console.WriteLine(String.Format("{0}  HasThumbnail     = {1}", padding, fileSpec.Thumbnail IsNot Nothing))
        If fileSpec.DataFields IsNot Nothing Then
            Console.WriteLine("    DataFields:")
            For Each name As String In fileSpec.DataFields.Keys
                Console.WriteLine(String.Format("      {0}={1}", name, fileSpec.DataFields(name).DataAsString))
            Next
        End If
    Next
End Sub

''' <summary>
''' Prints the portfolio sort properties.
''' </summary>
''' <param name="pdfAttachmentCollectionSchema">The PDF attachment collection sort properties.</param>
Private Shared Sub PrintPortfolioSort(sort As PdfAttachmentCollectionSort)
    Console.WriteLine("Sort:")
    Console.WriteLine("  Field names:")
    Dim fieldNames As String() = sort.FieldNames
    For i As Integer = 0 To fieldNames.Length - 1
        Console.WriteLine("    {0}: {1}", i, fieldNames(i))
    Next

    Console.WriteLine("  Ascending orders:")
    Dim ascendingOrders As Boolean() = sort.AscendingOrders
    For i As Integer = 0 To ascendingOrders.Length - 1
        Console.WriteLine("    {0}: {1}", i, ascendingOrders(i))
    Next
End Sub

''' <summary>
''' Prints the portfolio schema.
''' </summary>
''' <param name="pdfAttachmentCollectionSchema">The PDF attachment collection schema.</param>
Private Shared Sub PrintPortfolioSchema(schema As PdfAttachmentCollectionSchema)
    Console.WriteLine("Schema:")
    For Each key As String In schema.Keys
        Console.WriteLine(String.Format("  {0}:", key))
        Dim field As PdfAttachmentCollectionSchemaField = schema(key)
        Console.WriteLine("    DataType          = {0}", field.DataType)
        Console.WriteLine("    DisplayedName     = {0}", field.DisplayedName)
        Console.WriteLine("    IsSupportsEditing = {0}", field.IsSupportsEditing)
        Console.WriteLine("    IsVisible         = {0}", field.IsVisible)
        Console.WriteLine("    Order             = {0}", field.Order)
    Next
End Sub

