        Console.WriteLine("    {0}: {1}", i, fieldNames[i]);

    Console.WriteLine("  Ascending orders:");
    bool[] ascendingOrders = sort.AscendingOrders;
    for (int i = 0; i < ascendingOrders.Length; i++)
        Console.WriteLine("    {0}: {1}", i, ascendingOrders[i]);
}

